<script>
export default {
    mounted(){
        const gsap = this.$gsap

        const devAnim = gsap.timeline({
            scrollTrigger : {
                trigger: '#site-building',
                start : 'bottom bottom',
            },
        })

        const stackAnim = gsap.timeline({
            scrollTrigger : {
                trigger: '#stack-box',
                start : 'center bottom',
            },
        })

        devAnim
        .from('#site-search-bar',{ x:-200, opacity:0, duration:0.3, ease: 'Power4. easeOut'})
        .from('#site-blue-block',{ y:200, opacity:0, duration:0.3, ease: 'Power4. easeOut'})
        .from('#site-lime-block',{ x:200, opacity:0, duration:0.3, ease: 'Power4. easeOut'})
        .from('#site-phone-block',{ y:-200, opacity:0, duration:0.3, ease: 'Power4. easeOut'})

        stackAnim
        .to('.stack-lime-path', { duration: 0.8, css:{ "filter": "drop-shadow(0 0 5px rgba(208, 242, 38, 0.77)) drop-shadow(0 0 10px rgba(208, 242, 38, 0.77))"},yoyo:true, repeat:1, ease: 'Power4. easeOut' })
    },
}
</script>

